INSERT INTO "ejercicios" VALUES (1,'waist','body weight','https://v2.exercisedb.io/image/ZAX4K7K6MwFi9d','0001','3/4 sit-up','abs','"[\"hip flexors\",\"lower back\"]"','["Lie flat on your back with your knees bent and feet flat on the ground.","Place your hands behind your head with your elbows pointing outwards.","Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is at a 45-degree angle.","Pause for a moment at the top, then slowly lower your upper body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (2,'waist','body weight','https://v2.exercisedb.io/image/SuteD1Q6xRA6qq','0002','45Â° side bend','abs','"[\"obliques\"]"','["Stand with your feet shoulder-width apart and your arms extended straight down by your sides.","Keeping your back straight and your core engaged, slowly bend your torso to one side, lowering your hand towards your knee.","Pause for a moment at the bottom, then slowly return to the starting position.","Repeat on the other side.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (3,'waist','body weight','https://v2.exercisedb.io/image/8XPu25Ku6XUUAt','0003','air bike','abs','"[\"hip flexors\"]"','["Lie flat on your back with your hands placed behind your head.","Lift your legs off the ground and bend your knees at a 90-degree angle.","Bring your right elbow towards your left knee while simultaneously straightening your right leg.","Return to the starting position and repeat the movement on the opposite side, bringing your left elbow towards your right knee while straightening your left leg.","Continue alternating sides in a pedaling motion for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (4,'waist','body weight','https://v2.exercisedb.io/image/T6PUOk6w9B9qh5','0006','alternate heel touchers','abs','"[\"obliques\"]"','["Lie flat on your back with your knees bent and feet flat on the ground.","Extend your arms straight out to the sides, parallel to the ground.","Engaging your abs, lift your shoulders off the ground and reach your right hand towards your right heel.","Return to the starting position and repeat on the left side, reaching your left hand towards your left heel.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (5,'waist','body weight','https://v2.exercisedb.io/image/3uAosILbahdyg4','2355','arm slingers hanging bent knee legs','abs','"[\"shoulders\",\"back\"]"','["Hang from a pull-up bar with your arms fully extended and your knees bent at a 90-degree angle.","Engage your core and lift your knees towards your chest, bringing them as close to your elbows as possible.","Slowly lower your legs back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (6,'waist','body weight','https://v2.exercisedb.io/image/Qg0GNfyMTTVDq3','2333','arm slingers hanging straight legs','abs','"[\"shoulders\",\"back\"]"','["Hang from a pull-up bar with your arms fully extended and your legs straight down.","Engage your core and lift your legs up in front of you until they are parallel to the ground.","Hold for a moment at the top, then slowly lower your legs back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (7,'waist','body weight','https://v2.exercisedb.io/image/wdBx4wKCWb4Mgp','3204','arms overhead full sit-up (male)','abs','"[\"hip flexors\",\"lower back\"]"','["Lie flat on your back with your knees bent and feet flat on the ground.","Extend your arms overhead, keeping them straight.","Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is upright.","Pause for a moment at the top, then slowly lower your upper body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (8,'waist','assisted','https://v2.exercisedb.io/image/SSTLisBXMPnbuw','0011','assisted hanging knee raise','abs','"[\"hip flexors\"]"','["Hang from a pull-up bar with your arms fully extended and your palms facing away from you.","Engage your core muscles and lift your knees towards your chest, bending at the hips and knees.","Pause for a moment at the top of the movement, squeezing your abs.","Slowly lower your legs back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (9,'waist','assisted','https://v2.exercisedb.io/image/dCqE5RYxzNj4yt','0010','assisted hanging knee raise with throw down','abs','"[\"hip flexors\",\"lower back\"]"','["Hang from a pull-up bar with your arms fully extended and your palms facing away from you.","Engage your core and lift your knees towards your chest, keeping your legs together.","Once your knees are at chest level, explosively throw your legs down towards the ground, extending them fully.","Allow your legs to swing back up and repeat the movement for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (10,'waist','assisted','https://v2.exercisedb.io/image/DPvvDSvxydGyZJ','0012','assisted lying leg raise with lateral throw down','abs','"[\"hip flexors\",\"obliques\"]"','["Lie flat on your back with your legs extended and your arms by your sides.","Place your hands under your glutes for support.","Engage your abs and lift your legs off the ground, keeping them straight.","While keeping your legs together, lower them to one side until they are a few inches above the ground.","Pause for a moment, then lift your legs back to the starting position.","Repeat the movement to the other side.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:30:19','2024-10-29 16:30:19');
INSERT INTO "ejercicios" VALUES (11,'upper legs','leverage machine','https://v2.exercisedb.io/image/o3gJ5eE6D2lylb','0597','lever seated hip abduction','abductors','"[\"glutes\",\"hamstrings\"]"','["Adjust the seat height so that your knees are at a 90-degree angle.","Sit on the machine with your back against the backrest and your feet on the footrests.","Place your hands on the side handles for stability.","Engage your abductors and slowly push your legs apart, away from the midline of your body.","Pause for a moment at the end of the movement, then slowly bring your legs back together to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:54','2024-10-29 16:39:54');
INSERT INTO "ejercicios" VALUES (12,'upper legs','resistance band','https://v2.exercisedb.io/image/8ccZyocEUc6h4J','3006','resistance band seated hip abduction','abductors','"[\"glutes\",\"hamstrings\"]"','["Sit on a chair or bench with your back straight and feet flat on the ground.","Wrap the resistance band around your thighs, just above your knees.","Place your hands on the sides of the chair or bench for support.","Engage your abductors (outer thigh muscles) and slowly push your knees apart, against the resistance of the band.","Pause for a moment at the end of the movement, then slowly bring your knees back together.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:54','2024-10-29 16:39:54');
INSERT INTO "ejercicios" VALUES (13,'upper legs','body weight','https://v2.exercisedb.io/image/v1mQuQf8f1ypP5','1774','side bridge hip abduction','abductors','"[\"glutes\",\"obliques\"]"','["Lie on your side with your legs extended and stacked on top of each other.","Prop yourself up on your forearm, keeping your elbow directly below your shoulder.","Engage your core and lift your hips off the ground, creating a straight line from your head to your feet.","While keeping your core engaged, lift your top leg as high as possible without rotating your hips.","Pause for a moment at the top, then lower your leg back down.","Repeat for the desired number of repetitions, then switch sides."]','2024-10-29 16:39:54','2024-10-29 16:39:54');
INSERT INTO "ejercicios" VALUES (14,'upper legs','body weight','https://v2.exercisedb.io/image/-krc4A8JYLPc0G','0710','side hip abduction','abductors','"[\"glutes\",\"quadriceps\"]"','["Stand with your feet shoulder-width apart and your hands on your hips.","Shift your weight to one leg and lift the opposite leg out to the side, keeping it straight.","Pause for a moment at the top, then slowly lower your leg back down to the starting position.","Repeat on the other side.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:39:54','2024-10-29 16:39:54');
INSERT INTO "ejercicios" VALUES (15,'upper legs','body weight','https://v2.exercisedb.io/image/z9OmiZlCT7R1TL','1427','straight leg outer hip abductor','abductors','"[\"glutes\",\"hamstrings\"]"','["Lie on your side with your legs straight and stacked on top of each other.","Place your bottom arm under your head for support.","Engage your core and lift your top leg as high as possible without rotating your hips or leaning backward.","Pause for a moment at the top, then slowly lower your leg back down to the starting position.","Repeat for the desired number of repetitions, then switch sides."]','2024-10-29 16:39:54','2024-10-29 16:39:54');
INSERT INTO "ejercicios" VALUES (16,'upper legs','assisted','https://v2.exercisedb.io/image/OvnhnotEtNtXBe','1712','assisted side lying adductor stretch','adductors','"[\"hamstrings\",\"glutes\"]"','["Lie on your side with your legs straight and stacked on top of each other.","Bend your bottom leg slightly for stability.","Place your top foot on a stable surface, such as a bench or step.","Keeping your top leg straight, slowly lower it towards the ground, feeling a stretch in your inner thigh.","Hold the stretch for 20-30 seconds.","Return to the starting position and repeat on the other side."]','2024-10-29 16:39:55','2024-10-29 16:39:55');
INSERT INTO "ejercicios" VALUES (17,'upper legs','body weight','https://v2.exercisedb.io/image/iAa8Wz5KjPhYnx','1494','butterfly yoga pose','adductors','"[\"hamstrings\",\"groin\"]"','["Sit on the floor with your legs extended in front of you.","Bend your knees and bring the soles of your feet together, allowing your knees to fall out to the sides.","Hold onto your ankles or feet with your hands.","Sit up tall and lengthen your spine.","Gently press your knees down towards the floor, feeling a stretch in your inner thighs.","Hold this position for a few breaths.","To release, slowly bring your knees back up and extend your legs."]','2024-10-29 16:39:55','2024-10-29 16:39:55');
INSERT INTO "ejercicios" VALUES (18,'upper legs','cable','https://v2.exercisedb.io/image/YGuadePKCiRYx4','0168','cable hip adduction','adductors','"[\"glutes\",\"quadriceps\"]"','["Attach the ankle cuff to your ankle and stand facing the cable machine.","Position yourself far enough away from the machine so that there is tension on the cable.","Place your hands on the machine for support.","Keeping your leg straight, slowly move your leg across your body towards the midline.","Pause for a moment at the end of the movement, then slowly return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:55','2024-10-29 16:39:55');
INSERT INTO "ejercicios" VALUES (19,'upper legs','leverage machine','https://v2.exercisedb.io/image/bE3wuqHMy2YJfW','0598','lever seated hip adduction','adductors','"[\"hamstrings\",\"glutes\"]"','["Adjust the seat height and position yourself on the machine with your back against the backrest.","Place your feet on the footrests and grasp the handles for stability.","Engage your adductor muscles and slowly bring your legs together, squeezing your inner thighs.","Pause for a moment at the peak contraction, then slowly return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:55','2024-10-29 16:39:55');
INSERT INTO "ejercicios" VALUES (20,'upper legs','body weight','https://v2.exercisedb.io/image/wfBvF20eDJBNDf','3667','side lying hip adduction (male)','adductors','"[\"glutes\",\"hamstrings\"]"','["Lie on your side with your legs straight and stacked on top of each other.","Place your bottom arm under your head for support.","Engage your adductors and lift your top leg as high as possible without rotating your hips or leaning backward.","Pause for a moment at the top, then slowly lower your leg back down to the starting position.","Repeat for the desired number of repetitions, then switch sides."]','2024-10-29 16:39:55','2024-10-29 16:39:55');
INSERT INTO "ejercicios" VALUES (21,'upper legs','body weight','https://v2.exercisedb.io/image/QMbDQI7Rn87mU6','1775','side plank hip adduction','adductors','"[\"obliques\",\"glutes\"]"','["Start by lying on your side with your legs extended and stacked on top of each other.","Prop yourself up on your forearm, keeping your elbow directly below your shoulder.","Engage your core and lift your hips off the ground, creating a straight line from your head to your feet.","While maintaining the side plank position, lift your top leg towards the ceiling, keeping it straight.","Slowly lower your leg back down to the starting position.","Repeat for the desired number of repetitions, then switch sides."]','2024-10-29 16:39:55','2024-10-29 16:39:55');
INSERT INTO "ejercicios" VALUES (22,'upper arms','band','https://v2.exercisedb.io/image/9C6axNbPW5KLNG','0968','band alternating biceps curl','biceps','"[\"forearms\"]"','["Stand with your feet shoulder-width apart and hold the band with an underhand grip, palms facing up.","Keep your elbows close to your sides and slowly curl one arm up towards your shoulder, squeezing your biceps at the top.","Lower the arm back down to the starting position and repeat with the other arm.","Continue alternating arms for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (23,'upper arms','band','https://v2.exercisedb.io/image/hvJUKM16l16PkP','0976','band concentration curl','biceps','"[\"forearms\"]"','["Sit on a bench or chair with your legs spread apart and your feet flat on the ground.","Hold one end of the band in your hand and step on the other end with your foot on the same side.","Lean forward slightly and rest your elbow on the inside of your thigh, just above the knee.","With your palm facing up, slowly curl your hand towards your shoulder, keeping your upper arm stationary.","Pause for a moment at the top, then slowly lower your hand back down to the starting position.","Repeat for the desired number of repetitions, then switch sides."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (24,'upper arms','band','https://v2.exercisedb.io/image/HQekRYOcbs2cvs','0986','band one arm overhead biceps curl','biceps','"[\"forearms\",\"shoulders\"]"','["Stand with your feet shoulder-width apart and place one end of the band under your foot.","Hold the other end of the band with your arm fully extended overhead, palm facing forward.","Keeping your upper arm stationary, curl your forearm towards your shoulder, squeezing your biceps.","Pause for a moment at the top, then slowly lower your forearm back to the starting position.","Repeat for the desired number of repetitions, then switch arms."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (25,'upper arms','barbell','https://v2.exercisedb.io/image/k3AueaXNRE48sQ','0023','barbell alternate biceps curl','biceps','"[\"forearms\"]"','["Stand up straight with your feet shoulder-width apart and hold a barbell in each hand, palms facing forward.","Keep your upper arms stationary and exhale as you curl the weights while contracting your biceps.","Continue to raise the barbells until your biceps are fully contracted and the barbells are at shoulder level.","Hold the contracted position for a brief pause as you squeeze your biceps.","Inhale as you slowly begin to lower the barbells back to the starting position.","Repeat for the desired number of repetitions, alternating arms."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (26,'upper arms','barbell','https://v2.exercisedb.io/image/cTAHdAXUGS0CHW','2407','barbell biceps curl (with arm blaster)','biceps','"[\"forearms\"]"','["Stand up straight with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing up.","Place your upper arms against the arm blaster, keeping your elbows close to your torso.","Keeping your upper arms stationary, exhale and curl the weights while contracting your biceps.","Continue to raise the barbell until your biceps are fully contracted and the bar is at shoulder level.","Hold the contracted position for a brief pause as you squeeze your biceps.","Inhale and slowly begin to lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (27,'upper arms','barbell','https://v2.exercisedb.io/image/DVhC7mHZSGcIpG','0031','barbell curl','biceps','"[\"forearms\"]"','["Stand up straight with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing forward.","Keep your elbows close to your torso and exhale as you curl the weights while contracting your biceps.","Continue to raise the bar until your biceps are fully contracted and the bar is at shoulder level.","Hold the contracted position for a brief pause as you squeeze your biceps.","Inhale as you slowly begin to lower the bar back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (28,'upper arms','barbell','https://v2.exercisedb.io/image/K3qh5O5JLPZmZm','0038','barbell drag curl','biceps','"[\"forearms\"]"','["Stand with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing up.","Let the barbell hang at arm''s length in front of your thighs.","Keeping your upper arms stationary, curl the barbell up towards your chest by contracting your biceps.","Pause for a moment at the top, then slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (29,'upper arms','barbell','https://v2.exercisedb.io/image/HcyTUT5pi-giMc','0059','barbell lying preacher curl','biceps','"[\"forearms\"]"','["Sit on a preacher bench with your chest against the pad and your arms extended over the edge, holding a barbell with an underhand grip.","Keeping your upper arms stationary, exhale and curl the weights while contracting your biceps.","Continue to raise the bar until your biceps are fully contracted and the bar is at shoulder level.","Hold the contracted position for a brief pause as you squeeze your biceps.","Inhale and slowly begin to lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (30,'upper arms','barbell','https://v2.exercisedb.io/image/Qbxku7pUfNl2of','0070','barbell preacher curl','biceps','"[\"forearms\"]"','["Sit on a preacher bench with your upper arms resting on the pad and your chest against the support.","Grasp the barbell with an underhand grip, slightly wider than shoulder-width apart.","Keeping your upper arms stationary, exhale and curl the barbell up towards your shoulders.","Pause for a moment at the top, squeezing your biceps.","Inhale and slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (31,'upper arms','barbell','https://v2.exercisedb.io/image/x3wW-1abYnyYHh','0072','barbell prone incline curl','biceps','"[\"forearms\"]"','["Set up an incline bench at a 45-degree angle.","Lie face down on the bench with your chest and stomach resting against it.","Hold a barbell with an underhand grip, shoulder-width apart.","Extend your arms fully, allowing the barbell to hang down towards the floor.","Keeping your upper arms stationary, exhale and curl the weights while contracting your biceps.","Continue to raise the barbell until your biceps are fully contracted and the bar is at shoulder level.","Hold the contracted position for a brief pause as you squeeze your biceps.","Inhale and slowly begin to lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:56','2024-10-29 16:39:56');
INSERT INTO "ejercicios" VALUES (32,'lower legs','body weight','https://v2.exercisedb.io/image/R4SBy9fgNFKUfz','1368','ankle circles','calves','"[\"ankle stabilizers\"]"','["Sit on the ground with your legs extended in front of you.","Lift one leg off the ground and rotate your ankle in a circular motion.","Perform the desired number of circles in one direction, then switch to the other direction.","Repeat with the other leg."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (33,'lower legs','assisted','https://v2.exercisedb.io/image/9mYx59WOU1p5YX','1708','assisted lying calves stretch','calves','"[\"hamstrings\"]"','["Lie on your back with your legs extended.","Bend one knee and place your foot flat on the ground.","Using your hands or a towel, gently pull your toes towards your body, feeling a stretch in your calf.","Hold the stretch for 20-30 seconds.","Release the stretch and repeat on the other leg."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (34,'lower legs','band','https://v2.exercisedb.io/image/NRVWo43W3JJIzq','0999','band single leg calf raise','calves','"[\"ankles\",\"feet\"]"','["Stand with your feet hip-width apart and place the band around the ball of your left foot.","Hold onto a stable object for balance if needed.","Slowly raise your left heel off the ground, lifting your body weight onto the ball of your foot.","Pause for a moment at the top, then slowly lower your left heel back down to the starting position.","Repeat for the desired number of repetitions, then switch to the right leg."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (35,'lower legs','band','https://v2.exercisedb.io/image/zLVgQ8hqZVlWD2','1000','band single leg reverse calf raise','calves','"[\"hamstrings\",\"glutes\"]"','["Stand with your feet hip-width apart and place the band around the ball of your foot.","Hold onto a stable object for balance.","Slowly raise your heel off the ground, lifting your body weight onto the ball of your foot.","Pause for a moment at the top, then slowly lower your heel back down to the starting position.","Repeat for the desired number of repetitions, then switch to the other leg."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (36,'lower legs','band','https://v2.exercisedb.io/image/56GBn0bHWWQkix','1369','band two legs calf raise - (band under both legs) v. 2','calves','"[\"ankles\",\"feet\"]"','["Stand with your feet shoulder-width apart and place a resistance band under both feet.","Hold the ends of the band with your hands for stability.","Raise your heels off the ground as high as possible, using your calves.","Pause for a moment at the top, then slowly lower your heels back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (37,'lower legs','barbell','https://v2.exercisedb.io/image/y-1BAx4axiUzid','1370','barbell floor calf raise','calves','"[\"hamstrings\"]"','["Place a barbell on the floor in front of you.","Stand with the balls of your feet on the edge of the barbell, with your heels hanging off.","Hold onto a stable object for balance if needed.","Raise your heels as high as possible, using your calves to lift your body.","Pause for a moment at the top, then slowly lower your heels back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (38,'lower legs','barbell','https://v2.exercisedb.io/image/KC7smKBVze5u3v','0088','barbell seated calf raise','calves','"[\"hamstrings\",\"quadriceps\"]"','["Sit on a bench with your feet flat on the floor and a barbell resting on your thighs.","Place the balls of your feet on a raised platform, such as a block or step.","Position the barbell across your thighs and hold it securely with your hands.","Keeping your back straight and your core engaged, lift your heels off the ground by extending your ankles.","Pause for a moment at the top, then slowly lower your heels back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (39,'lower legs','barbell','https://v2.exercisedb.io/image/8P05HJLHnOwaBl','1371','barbell seated calf raise','calves','"[\"hamstrings\"]"','["Sit on a bench with your feet flat on the floor and a barbell resting on your thighs.","Place the balls of your feet on a raised platform, such as a block or step.","Lower your heels as far as possible, feeling a stretch in your calves.","Raise your heels as high as possible, contracting your calves.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (40,'lower legs','barbell','https://v2.exercisedb.io/image/HBuOvNDcMJ3FOV','1372','barbell standing calf raise','calves','"[\"hamstrings\",\"glutes\"]"','["Stand with your feet shoulder-width apart and place a barbell across your upper back.","Raise your heels off the ground as high as possible, using only your toes.","Pause for a moment at the top, then slowly lower your heels back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (41,'lower legs','barbell','https://v2.exercisedb.io/image/VJmtpqbRLtdkm0','0108','barbell standing leg calf raise','calves','"[\"hamstrings\",\"glutes\"]"','["Stand with your feet shoulder-width apart and place a barbell across your upper back.","Raise your heels off the ground as high as possible, using your calves.","Pause for a moment at the top, then slowly lower your heels back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:58','2024-10-29 16:39:58');
INSERT INTO "ejercicios" VALUES (42,'cardio','body weight','https://v2.exercisedb.io/image/QeXQSXVj46RtHV','3220','astride jumps (male)','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"calves\"]"','["Stand with your feet shoulder-width apart.","Bend your knees and lower your body into a squat position.","Jump explosively upwards, extending your legs and arms.","While in the air, spread your legs apart and bring your arms out to the sides.","Land softly with your feet shoulder-width apart, bending your knees to absorb the impact.","Repeat for the desired number of repetitions."]','2024-10-29 16:39:59','2024-10-29 16:39:59');
INSERT INTO "ejercicios" VALUES (43,'cardio','body weight','https://v2.exercisedb.io/image/QVwMjviaUMK3zI','3672','back and forth step','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"glutes\",\"calves\"]"','["Stand with your feet shoulder-width apart.","Step forward with your right foot, bending your knee and lowering your body into a lunge position.","Push off with your right foot and step back to the starting position.","Repeat the movement with your left foot, alternating legs with each step.","Continue stepping back and forth, maintaining a steady pace.","Repeat for the desired duration or number of repetitions."]','2024-10-29 16:39:59','2024-10-29 16:39:59');
INSERT INTO "ejercicios" VALUES (44,'cardio','body weight','https://v2.exercisedb.io/image/hXagBho5FoezEW','3360','bear crawl','cardiovascular system','"[\"core\",\"shoulders\",\"triceps\"]"','["Start on all fours with your hands directly under your shoulders and your knees directly under your hips.","Lift your knees slightly off the ground, keeping your back flat and your core engaged.","Move your right hand and left foot forward simultaneously, followed by your left hand and right foot.","Continue crawling forward, alternating your hand and foot movements.","Maintain a steady pace and keep your core tight throughout the exercise.","Continue for the desired distance or time."]','2024-10-29 16:39:59','2024-10-29 16:39:59');
INSERT INTO "ejercicios" VALUES (45,'cardio','body weight','https://v2.exercisedb.io/image/XFn8b1eJ4UbMKb','1160','burpee','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"calves\",\"shoulders\",\"chest\"]"','["Start in a standing position with your feet shoulder-width apart.","Lower your body into a squat position by bending your knees and placing your hands on the floor in front of you.","Kick your feet back into a push-up position.","Perform a push-up, keeping your body in a straight line.","Jump your feet back into the squat position.","Jump up explosively, reaching your arms overhead.","Land softly and immediately lower back into a squat position to begin the next repetition."]','2024-10-29 16:39:59','2024-10-29 16:39:59');
INSERT INTO "ejercicios" VALUES (46,'cardio','leverage machine','https://v2.exercisedb.io/image/GItAFTeTF71-3B','2331','cycle cross trainer','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"glutes\"]"','["Adjust the seat height and position yourself on the cycle cross trainer.","Place your feet on the pedals and grip the handlebars.","Start pedaling in a smooth and controlled motion.","Maintain a steady pace and increase the resistance if desired.","Continue pedaling for the desired duration of your cardio workout."]','2024-10-29 16:40:00','2024-10-29 16:40:00');
INSERT INTO "ejercicios" VALUES (47,'cardio','dumbbell','https://v2.exercisedb.io/image/VyIRguJLmsmwes','1201','dumbbell burpee','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"calves\",\"shoulders\",\"triceps\",\"core\"]"','["Start in a standing position with your feet shoulder-width apart and a dumbbell in each hand.","Lower your body into a squat position, placing the dumbbells on the ground in front of you.","Kick your feet back into a push-up position, keeping your body in a straight line.","Perform a push-up, bending your elbows and lowering your chest towards the ground.","Jump your feet back towards your hands, landing in a squat position.","Stand up explosively, lifting the dumbbells off the ground and bringing them to your shoulders.","Press the dumbbells overhead, fully extending your arms.","Lower the dumbbells back to your shoulders and repeat the entire sequence for the desired number of repetitions."]','2024-10-29 16:40:00','2024-10-29 16:40:00');
INSERT INTO "ejercicios" VALUES (48,'cardio','body weight','https://v2.exercisedb.io/image/hwvpX-HwMfQf6M','3221','half knee bends (male)','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"glutes\"]"','["Stand with your feet shoulder-width apart.","Bend your knees and lower your body down as if you were sitting back into a chair.","Keep your chest up and your weight in your heels.","Pause for a moment at the bottom, then push through your heels to return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:00','2024-10-29 16:40:00');
INSERT INTO "ejercicios" VALUES (49,'cardio','body weight','https://v2.exercisedb.io/image/uNgmuBrz37Agpg','3636','high knee against wall','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"glutes\",\"calves\"]"','["Stand facing a wall with your feet hip-width apart.","Place your hands on the wall for support.","Engage your core and lift your right knee up towards your chest, while keeping your left foot on the ground.","Quickly switch legs, bringing your left knee up towards your chest and lowering your right foot back down.","Continue alternating legs in a running motion, bringing your knees up as high as possible.","Maintain a fast pace and keep your upper body stable throughout the exercise.","Repeat for the desired duration or number of repetitions."]','2024-10-29 16:40:00','2024-10-29 16:40:00');
INSERT INTO "ejercicios" VALUES (50,'cardio','body weight','https://v2.exercisedb.io/image/HiSdq8WxTHu4DQ','0501','jack burpee','cardiovascular system','"[\"quadriceps\",\"hamstrings\",\"calves\",\"shoulders\",\"triceps\",\"core\"]"','["Start in a standing position with your feet shoulder-width apart.","Lower your body into a squat position, placing your hands on the ground in front of you.","Kick your feet back, landing in a push-up position.","Perform a push-up, lowering your chest to the ground and then pushing back up.","Jump your feet forward, landing in a squat position.","Jump up explosively, reaching your arms overhead.","Land softly and immediately lower back into the squat position to begin the next repetition."]','2024-10-29 16:40:00','2024-10-29 16:40:00');
INSERT INTO "ejercicios" VALUES (51,'cardio','body weight','https://v2.exercisedb.io/image/EO1-zau1xYTkkb','3224','jack jump (male)','cardiovascular system','"[\"quadriceps\",\"calves\"]"','["Stand with your feet together and your arms by your sides.","Jump up, spreading your feet apart and raising your arms above your head.","As you land, quickly jump back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:00','2024-10-29 16:40:00');
INSERT INTO "ejercicios" VALUES (52,'shoulders','band','https://v2.exercisedb.io/image/RiT8J7gGpKteat','0977','band front lateral raise','delts','"[\"traps\",\"upper back\"]"','["Stand with your feet shoulder-width apart and hold the band in front of your thighs with your palms facing down.","Keep your arms straight and lift the band up in front of you until your arms are parallel to the ground.","Pause for a moment at the top, then slowly lower the band back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (53,'shoulders','band','https://v2.exercisedb.io/image/QQ7SRwYHUY645F','0978','band front raise','delts','"[\"triceps\",\"upper back\"]"','["Stand with your feet shoulder-width apart and hold the band in front of your thighs with your palms facing down.","Keep your arms straight and slowly raise them forward until they are parallel to the ground.","Pause for a moment at the top, then slowly lower your arms back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (54,'shoulders','band','https://v2.exercisedb.io/image/cJu1hI4U4p9JOP','0993','band reverse fly','delts','"[\"upper back\",\"trapezius\"]"','["Attach the band to a stationary object at chest height.","Stand with your feet shoulder-width apart and hold the band with both hands in front of you.","Keep your arms straight and lift them out to the sides until they are parallel to the ground.","Squeeze your shoulder blades together at the top of the movement.","Slowly lower your arms back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (55,'shoulders','band','https://v2.exercisedb.io/image/3Re6fNMz7RGA8h','0997','band shoulder press','delts','"[\"triceps\",\"upper back\"]"','["Stand with your feet shoulder-width apart and place the band under your feet.","Hold the band with your palms facing forward and raise your hands to shoulder height, elbows bent.","Press the band overhead, fully extending your arms.","Pause for a moment at the top, then slowly lower the band back to shoulder height.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (56,'shoulders','band','https://v2.exercisedb.io/image/D50CefUXK1SoIN','1022','band standing rear delt row','delts','"[\"trapezius\",\"rhomboids\",\"biceps\"]"','["Stand with your feet shoulder-width apart and place the band under your feet.","Hold the band handles with your palms facing each other and your arms extended in front of you.","Bend your knees slightly and hinge forward at the hips, keeping your back straight.","Pull the band towards your chest, squeezing your shoulder blades together.","Pause for a moment at the top, then slowly release the tension and return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (57,'shoulders','band','https://v2.exercisedb.io/image/15C5PP1AuYLaSF','1012','band twisting overhead press','delts','"[\"triceps\",\"upper back\"]"','["Stand with your feet shoulder-width apart and place the band under your feet.","Hold the band handles at shoulder height with your palms facing forward.","Engage your core and press the band overhead, fully extending your arms.","As you press, twist your torso to one side, keeping your hips stable.","Pause for a moment at the top, then return to the starting position.","Repeat the press and twist on the opposite side.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (58,'shoulders','band','https://v2.exercisedb.io/image/BD5por6KHkV9C3','1017','band y-raise','delts','"[\"traps\",\"rhomboids\"]"','["Stand with your feet shoulder-width apart and hold the band in front of your thighs with your palms facing inwards.","Keep your arms straight and lift them up and out to the sides, forming a ''Y'' shape with your body.","Squeeze your shoulder blades together at the top of the movement.","Slowly lower your arms back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (59,'shoulders','barbell','https://v2.exercisedb.io/image/uNNRv-JW--xXKB','0041','barbell front raise','delts','"[\"biceps\",\"triceps\"]"','["Stand with your feet shoulder-width apart and hold a barbell in front of your thighs with an overhand grip.","Keep your arms straight and lift the barbell forward and upward until it reaches shoulder level.","Pause for a moment at the top, then slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (60,'shoulders','barbell','https://v2.exercisedb.io/image/ORdItmO2XKWz0f','0067','barbell one arm snatch','delts','"[\"traps\",\"forearms\",\"core\"]"','["Stand with your feet shoulder-width apart, toes pointing slightly outwards.","Hold the barbell with an overhand grip, hands slightly wider than shoulder-width apart.","Bend your knees and lower your hips into a squat position, keeping your back straight and chest up.","Explosively extend your hips, knees, and ankles, driving the barbell upwards.","As the barbell reaches chest level, pull it upwards with your arm, keeping it close to your body.","Rotate your elbow under the barbell and extend your arm fully overhead, locking out your elbow.","Lower the barbell back down to the starting position in a controlled manner.","Repeat for the desired number of repetitions, then switch arms."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (61,'shoulders','barbell','https://v2.exercisedb.io/image/TTqa20eKYHOIbC','0075','barbell rear delt raise','delts','"[\"traps\",\"rhomboids\"]"','["Stand with your feet shoulder-width apart and hold a barbell with an overhand grip, palms facing down.","Bend your knees slightly and hinge forward at the hips, keeping your back straight.","Raise the barbell out to the sides, keeping your arms straight, until they are parallel to the ground.","Pause for a moment at the top, then slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:01','2024-10-29 16:40:01');
INSERT INTO "ejercicios" VALUES (62,'lower arms','band','https://v2.exercisedb.io/image/5kHS4gq7vr4mpR','0994','band reverse wrist curl','forearms','"[\"forearms\"]"','["Sit on a bench or chair with your feet flat on the ground.","Hold the band with an overhand grip, palms facing down, and wrap it around your fingers.","Rest your forearms on your thighs, with your wrists hanging off the edge.","Slowly curl your wrists upward, squeezing your forearms.","Pause for a moment at the top, then slowly lower your wrists back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (63,'lower arms','band','https://v2.exercisedb.io/image/wKZvMMCZ1tPMPc','1016','band wrist curl','forearms','"[\"biceps\",\"triceps\"]"','["Sit on a bench or chair with your feet flat on the ground.","Hold the band with both hands, palms facing up, and rest your forearms on your thighs.","Slowly curl your wrists upward, squeezing your forearms.","Pause for a moment at the top, then slowly lower your wrists back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (64,'lower arms','barbell','https://v2.exercisedb.io/image/Ugpx9MlmHjADcE','1411','barbell palms down wrist curl over a bench','forearms','"[\"biceps\",\"brachialis\"]"','["Sit on a bench with your feet flat on the ground and your forearms resting on your thighs, palms facing down.","Hold a barbell with an overhand grip, hands shoulder-width apart.","Lower the barbell towards the ground by flexing your wrists, keeping your forearms stationary.","Pause for a moment at the bottom, then slowly raise the barbell back up by extending your wrists.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (65,'lower arms','barbell','https://v2.exercisedb.io/image/CL8CBN6Cjg7jBq','1412','barbell palms up wrist curl over a bench','forearms','"[\"biceps\",\"shoulders\"]"','["Sit on a bench with your feet flat on the ground and hold a barbell with an underhand grip, palms facing up.","Rest your forearms on the bench, allowing your wrists to hang off the edge.","Keeping your forearms stationary, exhale and curl your wrists upwards as far as possible.","Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (66,'lower arms','barbell','https://v2.exercisedb.io/image/H1k30HMKRVDx0u','0079','barbell revers wrist curl v. 2','forearms','"[\"biceps\",\"brachialis\"]"','["Sit on a bench with your feet flat on the ground and your knees bent.","Hold a barbell with an overhand grip, palms facing down, and your hands shoulder-width apart.","Rest your forearms on your thighs, allowing your wrists to hang off the edge.","Keeping your forearms stationary, exhale and curl your wrists upward as far as possible.","Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (67,'lower arms','barbell','https://v2.exercisedb.io/image/ufW4jbLFBGHbyA','0082','barbell reverse wrist curl','forearms','"[\"biceps\",\"brachialis\"]"','["Sit on a bench with your feet flat on the ground and hold a barbell with an overhand grip, palms facing down.","Rest your forearms on your thighs, allowing your wrists to hang off the edge.","Slowly curl your wrists upward, bringing the barbell towards your body.","Pause for a moment at the top, then slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (68,'lower arms','barbell','https://v2.exercisedb.io/image/vX06IL3zZryc2w','0104','barbell standing back wrist curl','forearms','"[\"biceps\",\"shoulders\"]"','["Stand up straight with your feet shoulder-width apart and hold a barbell with an overhand grip.","Rest the barbell on the back of your hands with your palms facing down and your fingers pointing towards your body.","Keeping your upper arms stationary, exhale and curl your wrists upwards as far as possible.","Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (69,'lower arms','barbell','https://v2.exercisedb.io/image/p6GED5hsUJFeNu','0126','barbell wrist curl','forearms','"[\"biceps\",\"brachialis\"]"','["Sit on a bench with your feet flat on the ground and your forearms resting on your thighs, holding a barbell with an underhand grip.","Allow the barbell to roll down to your fingertips, keeping your wrists straight.","Slowly curl the barbell up towards your forearms by flexing your wrists.","Pause for a moment at the top, then slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (70,'lower arms','barbell','https://v2.exercisedb.io/image/MMXKjjDNk7n7oL','0125','barbell wrist curl v. 2','forearms','"[\"biceps\",\"brachialis\"]"','["Sit on a bench with your feet flat on the ground and your knees bent.","Hold a barbell with an underhand grip, palms facing up, and your hands shoulder-width apart.","Rest your forearms on your thighs, allowing your wrists to hang off the edge.","Slowly curl your wrists upward, bringing the barbell towards your forearms.","Pause for a moment at the top, then slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (71,'lower arms','cable','https://v2.exercisedb.io/image/JJVx64Ks2IQGV5','0210','cable reverse wrist curl','forearms','"[\"forearms\",\"wrists\"]"','["Attach a cable to a low pulley and sit on a bench facing the cable machine.","Grasp the cable handle with an overhand grip, palms facing down.","Rest your forearms on your thighs, with your wrists hanging off the edge.","Keeping your forearms stationary, exhale and curl your wrists upward as far as possible.","Pause for a moment at the top, then inhale and slowly lower your wrists back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:02','2024-10-29 16:40:02');
INSERT INTO "ejercicios" VALUES (72,'upper legs','body weight','https://v2.exercisedb.io/image/Ls-Oyb59eqSoif','3214','arms apart circular toe touch (male)','glutes','"[\"hamstrings\",\"quadriceps\",\"calves\"]"','["Stand with your feet shoulder-width apart and arms extended to the sides.","Keeping your legs straight, bend forward at the waist and reach down towards your toes with your right hand.","As you reach down, simultaneously lift your left leg straight up behind you, maintaining balance.","Return to the starting position and repeat the movement with your left hand reaching towards your toes and your right leg lifting up behind you.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (73,'upper legs','assisted','https://v2.exercisedb.io/image/JODiXEO9IUYNLt','1709','assisted lying glutes stretch','glutes','"[\"hamstrings\"]"','["Lie on your back with your legs extended.","Bend your right knee and place your right ankle on your left thigh, just above the knee.","Grasp your left thigh with both hands and gently pull it towards your chest.","Hold the stretch for 20-30 seconds.","Release and repeat on the other side."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (74,'upper legs','assisted','https://v2.exercisedb.io/image/ni4cyWXCLyiOWo','1710','assisted lying gluteus and piriformis stretch','glutes','"[\"hamstrings\"]"','["Lie on your back with your legs extended.","Bend your right knee and place your right ankle on your left thigh, just above the knee.","Grasp your left thigh with both hands and gently pull it towards your chest.","Hold the stretch for 20-30 seconds.","Release the stretch and repeat on the other side."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (75,'upper legs','band','https://v2.exercisedb.io/image/E9SiVSthn9h2XQ','0980','band bent-over hip extension','glutes','"[\"hamstrings\",\"lower back\"]"','["Attach the band to a sturdy anchor point at ankle height.","Stand facing away from the anchor point with your feet shoulder-width apart.","Step back to create tension in the band, keeping your knees slightly bent.","Hinge at the hips and lean forward, maintaining a neutral spine.","Extend your right leg straight back, squeezing your glutes at the top.","Lower your right leg back down and repeat with the left leg.","Continue alternating legs for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (76,'upper legs','band','https://v2.exercisedb.io/image/tdnSkE5pPpIUoR','1408','band hip lift','glutes','"[\"hamstrings\",\"quadriceps\"]"','["Lie on your back with your knees bent and feet flat on the ground.","Place a resistance band just above your knees.","Engage your glutes and core muscles.","Press your heels into the ground and lift your hips off the floor, squeezing your glutes at the top.","Pause for a moment at the top, then slowly lower your hips back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (77,'upper legs','band','https://v2.exercisedb.io/image/rUpmPRPxHwesmc','0984','band lying hip internal rotation','glutes','"[\"hamstrings\",\"quadriceps\"]"','["Lie on your back with your legs straight and a resistance band looped around your feet.","Bend your knees and bring them towards your chest, keeping your feet together.","Slowly rotate your knees outwards, away from each other, while keeping your feet together.","Pause for a moment at the end of the rotation, then slowly return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (78,'upper legs','band','https://v2.exercisedb.io/image/ti7EqwNzWEioPm','0991','band pull through','glutes','"[\"hamstrings\",\"lower back\"]"','["Attach a resistance band to a sturdy anchor point at ground level.","Stand facing away from the anchor point with your feet shoulder-width apart.","Step forward to create tension in the band, keeping your knees slightly bent.","Hinge at the hips and push your glutes back, maintaining a slight bend in your knees.","Lower your torso until it is parallel to the ground, feeling a stretch in your hamstrings.","Drive your hips forward and squeeze your glutes to return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (79,'upper legs','band','https://v2.exercisedb.io/image/dwI2RVqtefI4u0','0996','band seated hip internal rotation','glutes','"[\"hamstrings\",\"quadriceps\"]"','["Sit on a chair or bench with your back straight and feet flat on the ground.","Place a resistance band around your thighs, just above your knees.","Keep your knees bent at a 90-degree angle and your feet shoulder-width apart.","Engage your glutes and slowly rotate your knees outward, pushing against the resistance of the band.","Pause for a moment at the end of the movement, then slowly return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (80,'upper legs','band','https://v2.exercisedb.io/image/6-OeCGK8rcTFX4','1004','band squat','glutes','"[\"quadriceps\",\"hamstrings\",\"calves\"]"','["Stand with your feet shoulder-width apart, with the band placed just above your knees.","Keeping your chest up and core engaged, push your hips back and bend your knees to lower into a squat position.","Make sure your knees are tracking over your toes and your weight is in your heels.","Pause for a moment at the bottom, then push through your heels to return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (81,'upper legs','band','https://v2.exercisedb.io/image/heQoFm9JMZ84J6','1003','band squat row','glutes','"[\"hamstrings\",\"quadriceps\",\"back\"]"','["Attach the band to a sturdy anchor point at waist height.","Stand facing the anchor point with your feet shoulder-width apart.","Hold the band handles with your palms facing each other and your arms extended in front of you.","Bend your knees and lower into a squat position, keeping your back straight and chest lifted.","From the squat position, pull the band handles towards your body, squeezing your shoulder blades together.","Pause for a moment at the top, then slowly release the tension and return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:03','2024-10-29 16:40:03');
INSERT INTO "ejercicios" VALUES (82,'upper legs','assisted','https://v2.exercisedb.io/image/FGR9FjOL-L82kt','0016','assisted prone hamstring','hamstrings','"[\"glutes\",\"lower back\"]"','["Lie face down on a mat or bench with your legs fully extended.","Have a partner or use a resistance band to secure your ankles.","Engage your hamstrings and lift your legs towards your glutes, keeping your knees straight.","Pause for a moment at the top, then slowly lower your legs back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (83,'upper legs','barbell','https://v2.exercisedb.io/image/C-I82Zn9V6B61C','0044','barbell good morning','hamstrings','"[\"lower back\"]"','["Start by standing with your feet shoulder-width apart and the barbell resting on your upper back.","Keeping your back straight and your core engaged, hinge forward at the hips, pushing your buttocks back as if you were trying to touch the wall behind you with your glutes.","Lower your torso until it is parallel to the ground, feeling a stretch in your hamstrings.","Pause for a moment, then return to the starting position by squeezing your glutes and pushing your hips forward.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (84,'upper legs','barbell','https://v2.exercisedb.io/image/uMtE7vpiz7kFHt','0116','barbell straight leg deadlift','hamstrings','"[\"glutes\",\"lower back\"]"','["Stand with your feet shoulder-width apart and your toes pointing forward.","Hold the barbell with an overhand grip, hands slightly wider than shoulder-width apart.","Bend at your hips and lower the barbell towards the ground, keeping your back straight and your knees slightly bent.","Lower the barbell until you feel a stretch in your hamstrings.","Engage your hamstrings and glutes to lift the barbell back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (85,'upper legs','cable','https://v2.exercisedb.io/image/l-Fb4RWxNYYUgQ','3235','cable assisted inverse leg curl','hamstrings','"[\"glutes\",\"calves\"]"','["Adjust the cable machine so that the ankle attachment is at the lowest setting.","Lie face down on the bench with your legs straight and the ankle attachment secured to your ankles.","Hold onto the handles of the bench for stability.","Keeping your upper body stationary, exhale and curl your legs up towards your glutes by flexing your knees.","Pause for a moment at the top of the movement, squeezing your hamstrings.","Inhale and slowly lower your legs back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (86,'upper legs','dumbbell','https://v2.exercisedb.io/image/vOcFcHrYdwUtKr','0339','dumbbell lying femoral','hamstrings','"[\"glutes\"]"','["Lie flat on your back with your legs extended and a dumbbell resting on your lower abdomen.","Bend your knees and bring the dumbbell towards your glutes, keeping your feet flat on the ground.","Pause for a moment at the top, then slowly lower the dumbbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (87,'upper legs','stability ball','https://v2.exercisedb.io/image/L1ApfG5-Jeshf6','1560','exercise ball seated hamstring stretch','hamstrings','"[\"glutes\",\"lower back\"]"','["Sit on the stability ball with your feet flat on the ground and your knees bent at a 90-degree angle.","Slowly roll the ball forward, walking your feet out until your upper back is resting on the ball and your legs are extended straight in front of you.","Place your hands on your hips for support.","Engage your core and slowly lower your upper body towards the ground, keeping your back straight and your chest lifted.","Stop when you feel a stretch in your hamstrings, and hold the position for 20-30 seconds.","Slowly return to the starting position by pushing through your heels and using your hamstrings to pull yourself back up.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (88,'upper legs','body weight','https://v2.exercisedb.io/image/yUnx7rZ32oFfxu','3193','glute-ham raise','hamstrings','"[\"glutes\",\"lower back\"]"','["Adjust the glute-ham raise machine to fit your body.","Position yourself face down on the machine with your ankles secured.","Place your hands on your chest or cross them over your chest.","Engage your hamstrings and glutes to lift your upper body up towards the ceiling.","Continue lifting until your body is in a straight line from your head to your heels.","Pause for a moment at the top, then slowly lower your body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (89,'upper legs','body weight','https://v2.exercisedb.io/image/Vh7Q1LoF6WFkHd','1511','hamstring stretch','hamstrings','"[\"glutes\"]"','["Stand with your feet shoulder-width apart.","Step forward with your right foot and shift your weight onto your right leg.","Keeping your back straight, slowly bend forward at the hips, reaching towards your right foot with both hands.","Hold the stretch for 20-30 seconds, then return to the starting position.","Repeat on the other side."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (90,'upper legs','body weight','https://v2.exercisedb.io/image/tf-JbVy6qzJe-R','0496','inverse leg curl (bench support)','hamstrings','"[\"glutes\",\"calves\"]"','["Lie face down on a bench with your hips at the edge and your legs extended straight behind you.","Hold onto the bench for support.","Keeping your upper body still, bend your knees and curl your legs towards your glutes.","Pause for a moment at the top, then slowly extend your legs back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (91,'upper legs','body weight','https://v2.exercisedb.io/image/tvtc4oB3yhqBGP','2400','inverse leg curl (on pull-up cable machine)','hamstrings','"[\"glutes\",\"calves\"]"','["Adjust the cable machine so that the ankle straps are at the lowest setting.","Lie face down on the bench with your legs extended and the ankle straps attached to your feet.","Hold onto the handles of the bench for stability.","Bend your knees and curl your legs towards your glutes, squeezing your hamstrings.","Pause for a moment at the top of the movement, then slowly lower your legs back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:05','2024-10-29 16:40:05');
INSERT INTO "ejercicios" VALUES (92,'back','cable','https://v2.exercisedb.io/image/zHs9WpUTW8bbTk','0007','alternate lateral pulldown','lats','"[\"biceps\",\"rhomboids\"]"','["Sit on the cable machine with your back straight and feet flat on the ground.","Grasp the handles with an overhand grip, slightly wider than shoulder-width apart.","Lean back slightly and pull the handles towards your chest, squeezing your shoulder blades together.","Pause for a moment at the peak of the movement, then slowly release the handles back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (93,'back','body weight','https://v2.exercisedb.io/image/euw8PRCYKoxS-6','3293','archer pull up','lats','"[\"biceps\",\"forearms\"]"','["Start by hanging from a pull-up bar with an overhand grip, slightly wider than shoulder-width apart.","Engage your core and pull your shoulder blades down and back.","As you pull yourself up, bend one arm and bring your elbow towards your side, while keeping the other arm straight.","Continue pulling until your chin is above the bar and your bent arm is fully flexed.","Lower yourself back down with control, straightening the bent arm and repeating the movement on the other side.","Alternate sides with each repetition."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (94,'back','leverage machine','https://v2.exercisedb.io/image/NdTxXMlb9XwiEa','0015','assisted parallel close grip pull-up','lats','"[\"biceps\",\"forearms\"]"','["Adjust the machine to your desired weight and height.","Place your hands on the parallel bars with a close grip, palms facing each other.","Hang from the bars with your arms fully extended and your feet off the ground.","Engage your back muscles and pull your body up towards the bars, keeping your elbows close to your body.","Continue pulling until your chin is above the bars.","Pause for a moment at the top, then slowly lower your body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (95,'back','leverage machine','https://v2.exercisedb.io/image/2qfc8yNjRk6FzX','0017','assisted pull-up','lats','"[\"biceps\",\"forearms\"]"','["Adjust the machine to your desired weight and height settings.","Grasp the handles with an overhand grip, slightly wider than shoulder-width apart.","Hang with your arms fully extended and your feet off the ground.","Engage your back muscles and pull your body up towards the handles, keeping your elbows close to your body.","Continue pulling until your chin is above the handles.","Pause for a moment at the top, then slowly lower your body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (96,'back','leverage machine','https://v2.exercisedb.io/image/TtTaOvwVhIG-dS','1431','assisted standing chin-up','lats','"[\"biceps\",\"forearms\"]"','["Adjust the machine to your desired assistance level.","Stand on the foot platform and grip the handles with an overhand grip, slightly wider than shoulder-width apart.","Keep your chest up and shoulders back, engage your core, and slightly bend your knees.","Pull your body up by flexing your elbows and driving your elbows down towards your sides.","Continue pulling until your chin is above the bar.","Pause for a moment at the top, then slowly lower your body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (97,'back','leverage machine','https://v2.exercisedb.io/image/kF9cUdobuYTcJe','1432','assisted standing pull-up','lats','"[\"biceps\",\"forearms\"]"','["Adjust the machine to your desired weight and height settings.","Stand facing the machine with your feet shoulder-width apart.","Grasp the handles with an overhand grip, slightly wider than shoulder-width apart.","Engage your lats and biceps, and pull yourself up towards the handles.","Pause for a moment at the top, squeezing your back muscles.","Slowly lower yourself back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (98,'back','body weight','https://v2.exercisedb.io/image/AVDORUi965ez34','1405','back pec stretch','lats','"[\"shoulders\",\"chest\"]"','["Stand tall with your feet shoulder-width apart.","Extend your arms straight out in front of you, parallel to the ground.","Cross your arms in front of your body, with your right arm over your left arm.","Interlock your fingers and rotate your palms away from your body.","Slowly raise your arms up and away from your body, feeling a stretch in your back and chest.","Hold the stretch for 15-30 seconds, then release.","Repeat on the opposite side."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (99,'back','band','https://v2.exercisedb.io/image/eG9yioplMwf7cC','0970','band assisted pull-up','lats','"[\"biceps\",\"forearms\"]"','["Attach the band to a pull-up bar or sturdy anchor point.","Step onto the band and grip the bar with your palms facing away from you, hands slightly wider than shoulder-width apart.","Hang with your arms fully extended, keeping your core engaged and your shoulders down and back.","Pull your body up towards the bar by squeezing your shoulder blades together and driving your elbows down towards your hips.","Continue pulling until your chin is above the bar, then slowly lower yourself back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (100,'back','band','https://v2.exercisedb.io/image/FnjjHxStptMpE5','0974','band close-grip pulldown','lats','"[\"biceps\",\"forearms\"]"','["Attach the band to a high anchor point, such as a pull-up bar or sturdy beam.","Stand facing the anchor point and grab the band with an underhand grip, hands shoulder-width apart.","Step back to create tension in the band, keeping your feet hip-width apart.","Engage your core and keep your back straight as you pull the band down towards your chest, squeezing your shoulder blades together.","Pause for a moment at the bottom of the movement, then slowly release the band back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (101,'back','band','https://v2.exercisedb.io/image/MWIS4Q4gsaw4dm','3117','band fixed back close grip pulldown','lats','"[\"biceps\",\"forearms\"]"','["Attach the band to a fixed point above you, such as a pull-up bar or sturdy beam.","Sit down on a bench or chair facing the band, with your feet flat on the ground and your knees bent.","Grasp the band with a close grip, palms facing towards you.","Keep your back straight and engage your core.","Pull the band down towards your chest, squeezing your shoulder blades together.","Pause for a moment at the bottom of the movement, feeling the contraction in your lats.","Slowly release the band back to the starting position, maintaining control throughout the movement.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:06','2024-10-29 16:40:06');
INSERT INTO "ejercicios" VALUES (102,'neck','body weight','https://v2.exercisedb.io/image/d1IT0hH8RfWqMS','1403','neck side stretch','levator scapulae','"[\"trapezius\",\"sternocleidomastoid\"]"','["Stand or sit up straight with your shoulders relaxed.","Tilt your head to one side, bringing your ear towards your shoulder.","Hold the stretch for 15-30 seconds.","Repeat on the other side.","Perform 2-4 sets on each side."]','2024-10-29 16:40:07','2024-10-29 16:40:07');
INSERT INTO "ejercicios" VALUES (103,'neck','body weight','https://v2.exercisedb.io/image/BU40qRUjHmhr1O','0716','side push neck stretch','levator scapulae','"[\"trapezius\",\"sternocleidomastoid\"]"','["Stand or sit up straight with your shoulders relaxed.","Tilt your head to the right, bringing your right ear towards your right shoulder.","Place your right hand on the left side of your head and gently apply pressure to increase the stretch.","Hold the stretch for 15-30 seconds.","Repeat on the other side, tilting your head to the left and applying pressure with your left hand.","Repeat the stretch 2-3 times on each side."]','2024-10-29 16:40:07','2024-10-29 16:40:07');
INSERT INTO "ejercicios" VALUES (104,'chest','body weight','https://v2.exercisedb.io/image/wQJPT3MHzMq9Zv','3294','archer push up','pectorals','"[\"triceps\",\"shoulders\",\"core\"]"','["Start in a push-up position with your hands slightly wider than shoulder-width apart.","Extend one arm straight out to the side, parallel to the ground.","Lower your body by bending your elbows, keeping your back straight and core engaged.","Push back up to the starting position.","Repeat on the other side, extending the opposite arm out to the side.","Continue alternating sides for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (105,'chest','leverage machine','https://v2.exercisedb.io/image/KE0BFylZZsFc2O','0009','assisted chest dip (kneeling)','pectorals','"[\"triceps\",\"shoulders\"]"','["Adjust the machine to your desired height and secure your knees on the pad.","Grasp the handles with your palms facing down and your arms fully extended.","Lower your body by bending your elbows until your upper arms are parallel to the floor.","Pause for a moment, then push yourself back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (106,'chest','assisted','https://v2.exercisedb.io/image/jCD7VjSJEE3vGD','1716','assisted seated pectoralis major stretch with stability ball','pectorals','"[\"shoulders\",\"triceps\"]"','["Sit on a stability ball with your feet flat on the ground and your back straight.","Hold a stability ball with both hands and extend your arms straight out in front of you.","Slowly lower the stability ball towards your chest, feeling a stretch in your pectoral muscles.","Hold the stretch for a few seconds, then slowly return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (107,'chest','leverage machine','https://v2.exercisedb.io/image/btF-Wn8E5PgVSf','2364','assisted wide-grip chest dip (kneeling)','pectorals','"[\"triceps\",\"shoulders\"]"','["Adjust the machine to your desired height and secure your knees on the pad.","Grasp the handles with a wide grip and keep your elbows slightly bent.","Lower your body by bending your elbows until your upper arms are parallel to the floor.","Push yourself back up to the starting position by extending your arms.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (108,'chest','band','https://v2.exercisedb.io/image/91kDh6gxDtqCJV','1254','band bench press','pectorals','"[\"triceps\",\"shoulders\"]"','["Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.","Grasp the band handles with an overhand grip, slightly wider than shoulder-width apart.","Extend your arms fully, pushing the bands away from your chest.","Slowly lower the bands back down to your chest, keeping your elbows at a 90-degree angle.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (109,'chest','band','https://v2.exercisedb.io/image/jERhIUh6UfH4Q0','0989','band one arm twisting chest press','pectorals','"[\"shoulders\",\"triceps\"]"','["Attach the band to a sturdy anchor point at chest height.","Stand with your side facing the anchor point and grab the band with one hand.","Step away from the anchor point to create tension in the band.","Position your feet shoulder-width apart and slightly bend your knees.","Bring your hand holding the band across your body, towards the opposite shoulder.","While maintaining tension in the band, push your hand forward and away from your body, extending your arm.","Slowly return to the starting position and repeat for the desired number of repetitions.","Switch sides and repeat the exercise with the other hand."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (110,'chest','barbell','https://v2.exercisedb.io/image/5bmVxAsXtbHcG9','0025','barbell bench press','pectorals','"[\"triceps\",\"shoulders\"]"','["Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.","Grasp the barbell with an overhand grip slightly wider than shoulder-width apart.","Lift the barbell off the rack and hold it directly above your chest with your arms fully extended.","Lower the barbell slowly towards your chest, keeping your elbows tucked in.","Pause for a moment when the barbell touches your chest.","Push the barbell back up to the starting position by extending your arms.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (111,'chest','barbell','https://v2.exercisedb.io/image/qA6gIlJ2yaaqaO','0033','barbell decline bench press','pectorals','"[\"triceps\",\"shoulders\"]"','["Lie on a decline bench with your feet secured and your head lower than your hips.","Grasp the barbell with an overhand grip slightly wider than shoulder-width apart.","Unrack the barbell and lower it slowly towards your chest, keeping your elbows tucked in.","Pause for a moment at the bottom, then push the barbell back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (112,'chest','barbell','https://v2.exercisedb.io/image/ISAD0yD33Fp2GD','1255','barbell decline pullover','pectorals','"[\"triceps\",\"shoulders\"]"','["Lie down on a decline bench with your head lower than your hips and your feet secured.","Hold the barbell with a pronated grip (palms facing away from you) and your hands slightly wider than shoulder-width apart.","Extend your arms above your chest, keeping a slight bend in your elbows.","Lower the barbell in an arc motion behind your head, feeling a stretch in your chest and shoulders.","Pause for a moment, then return the barbell to the starting position by reversing the motion.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (113,'chest','barbell','https://v2.exercisedb.io/image/nK0cJT26m8Jyd7','0036','barbell decline wide-grip press','pectorals','"[\"triceps\",\"shoulders\"]"','["Lie on a decline bench with your feet secured and your head lower than your hips.","Grasp the barbell with a wide grip, slightly wider than shoulder-width apart.","Lower the barbell to your chest, keeping your elbows out to the sides.","Push the barbell back up to the starting position, fully extending your arms.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:08','2024-10-29 16:40:08');
INSERT INTO "ejercicios" VALUES (114,'upper legs','body weight','https://v2.exercisedb.io/image/msFmmdshOGg9hk','1512','all fours squad stretch','quads','"[\"hamstrings\",\"glutes\"]"','["Start on all fours with your hands directly under your shoulders and your knees directly under your hips.","Extend one leg straight back, keeping your knee bent and your foot flexed.","Slowly lower your hips towards the ground, feeling a stretch in your quads.","Hold this position for 20-30 seconds.","Switch legs and repeat the stretch on the other side."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (115,'upper legs','assisted','https://v2.exercisedb.io/image/CVbyuLOmZWGQSA','1713','assisted prone lying quads stretch','quads','"[\"hamstrings\",\"glutes\"]"','["Lie face down on the ground with your legs extended.","Bend your left knee and reach back with your left hand to grab your left foot or ankle.","Gently pull your left foot towards your glutes, feeling a stretch in your left quad.","Hold the stretch for 20-30 seconds, then release.","Repeat with your right leg."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (116,'upper legs','body weight','https://v2.exercisedb.io/image/1m9qFMMd1-t8wM','1473','backward jump','quads','"[\"hamstrings\",\"glutes\",\"calves\"]"','["Stand with your feet shoulder-width apart.","Bend your knees slightly and jump backwards, pushing off with both feet.","Land softly on the balls of your feet, bending your knees to absorb the impact.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (117,'upper legs','body weight','https://v2.exercisedb.io/image/vqkUSdam6DQPTB','0020','balance board','quads','"[\"calves\",\"hamstrings\",\"glutes\"]"','["Place the balance board on a flat surface.","Step onto the balance board with one foot, ensuring it is centered.","Slowly shift your weight onto the foot on the balance board, keeping your core engaged.","Maintain your balance and stability as you hold the position for a desired amount of time.","Repeat the exercise with the other foot."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (118,'upper legs','band','https://v2.exercisedb.io/image/KSqlKl3L0NnLgw','0987','band one arm single leg split squat','quads','"[\"glutes\",\"hamstrings\"]"','["Stand with your feet hip-width apart and place a resistance band around your ankles.","Extend one leg forward and rest the top of your foot on a bench or step behind you.","Hold onto a support with one hand for balance.","Bend your standing leg and lower your body down into a squat position, keeping your knee in line with your toes.","Push through your heel to return to the starting position.","Repeat for the desired number of repetitions, then switch legs."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (119,'upper legs','band','https://v2.exercisedb.io/image/qHxy2vqyuhpTtg','1001','band single leg split squat','quads','"[\"glutes\",\"hamstrings\"]"','["Stand with your feet hip-width apart and place a resistance band around your ankles.","Take a big step forward with your right foot and a smaller step back with your left foot.","Bend your knees and lower your body until your right thigh is parallel to the ground, keeping your left knee slightly above the ground.","Push through your right heel to return to the starting position.","Repeat on the other side."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (120,'upper legs','barbell','https://v2.exercisedb.io/image/laiangpvtqJIxB','0024','barbell bench front squat','quads','"[\"hamstrings\",\"glutes\",\"calves\"]"','["Start by standing with your feet shoulder-width apart and the barbell resting on your upper chest, just below your collarbone.","Hold the barbell with an overhand grip, keeping your elbows up and your upper arms parallel to the ground.","Lower your body down into a squat position by bending at the knees and hips, keeping your back straight and your chest up.","Pause for a moment at the bottom of the squat, then push through your heels to return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (121,'upper legs','barbell','https://v2.exercisedb.io/image/dwD2Fbzgmu64wn','0026','barbell bench squat','quads','"[\"glutes\",\"hamstrings\",\"calves\"]"','["Set up a barbell on a squat rack at chest height.","Stand facing away from the rack, with your feet shoulder-width apart.","Bend your knees and lower your body down into a squat position, keeping your back straight and chest up.","Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.","Lift the barbell off the rack and step back, ensuring your feet are still shoulder-width apart.","Lower your body down into a squat, keeping your knees in line with your toes.","Pause for a moment at the bottom, then push through your heels to return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (122,'upper legs','barbell','https://v2.exercisedb.io/image/z8dU5E3CStkf7H','0028','barbell clean and press','quads','"[\"hamstrings\",\"glutes\",\"shoulders\",\"triceps\"]"','["Stand with your feet shoulder-width apart and the barbell on the floor in front of you.","Bend your knees and hinge at the hips to lower down and grip the barbell with an overhand grip, hands slightly wider than shoulder-width apart.","Drive through your heels and extend your hips and knees to lift the barbell off the floor, keeping it close to your body.","As the barbell reaches your thighs, explosively extend your hips, shrug your shoulders, and pull the barbell up towards your chest.","As the barbell reaches chest height, quickly drop under it and catch it at shoulder level, with your elbows pointing forward and your palms facing up.","From the catch position, press the barbell overhead by extending your arms and pushing the barbell straight up.","Lower the barbell back down to the starting position and repeat for the desired number of repetitions."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (123,'upper legs','barbell','https://v2.exercisedb.io/image/ljqttMOR5huxbo','0068','barbell one leg squat','quads','"[\"glutes\",\"hamstrings\",\"calves\"]"','["Stand with your feet shoulder-width apart and hold a barbell across your upper back.","Lift one foot off the ground and extend it forward, keeping it parallel to the ground.","Bend your standing leg and lower your body down as if sitting back into a chair, keeping your chest up and your back straight.","Lower yourself until your thigh is parallel to the ground, then push through your heel to return to the starting position.","Repeat for the desired number of repetitions, then switch legs and repeat."]','2024-10-29 16:40:09','2024-10-29 16:40:09');
INSERT INTO "ejercicios" VALUES (124,'chest','barbell','https://v2.exercisedb.io/image/sJy7Opz32L6eAB','0050','barbell incline shoulder raise','serratus anterior','"[\"deltoids\",\"trapezius\"]"','["Set up an incline bench at a 45-degree angle.","Sit on the bench with your back against the pad and feet flat on the ground.","Hold a barbell with an overhand grip, slightly wider than shoulder-width apart.","Lift the barbell up to shoulder height, keeping your elbows slightly bent.","Slowly raise the barbell overhead, extending your arms fully.","Pause for a moment at the top, then slowly lower the barbell back to shoulder height.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:10','2024-10-29 16:40:10');
INSERT INTO "ejercicios" VALUES (125,'chest','dumbbell','https://v2.exercisedb.io/image/J2ScKloiCsv4y8','0328','dumbbell incline shoulder raise','serratus anterior','"[\"deltoids\",\"trapezius\"]"','["Set an incline bench to a 45-degree angle and sit on it with a dumbbell in each hand, palms facing down.","Rest the dumbbells on your thighs and lean back onto the bench.","Use your thighs to help raise the dumbbells to shoulder height, then rotate your wrists so that your palms are facing forward.","Exhale and slowly raise the dumbbells above your head, keeping a slight bend in your elbows.","Pause for a moment at the top, then inhale and slowly lower the dumbbells back to shoulder height.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:10','2024-10-29 16:40:10');
INSERT INTO "ejercicios" VALUES (126,'chest','body weight','https://v2.exercisedb.io/image/nwLhBVHE1ZPcWA','3011','incline scapula push up','serratus anterior','"[\"triceps\",\"shoulders\"]"','["Set up an incline bench at a 45-degree angle.","Place your hands on the bench slightly wider than shoulder-width apart.","Position your feet on the ground, hip-width apart.","Lower your chest towards the bench, keeping your elbows tucked in.","As you lower, retract your shoulder blades, squeezing your scapulae together.","Push through your palms to extend your arms and return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:10','2024-10-29 16:40:10');
INSERT INTO "ejercicios" VALUES (127,'chest','body weight','https://v2.exercisedb.io/image/fF8tRpBf-gogLI','3021','scapula push-up','serratus anterior','"[\"triceps\",\"shoulders\"]"','["Start in a high plank position with your hands directly under your shoulders and your body in a straight line.","Lower your chest towards the ground, keeping your elbows close to your body.","As you lower, squeeze your shoulder blades together and push your chest forward.","Pause for a moment at the bottom, then push back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:10','2024-10-29 16:40:10');
INSERT INTO "ejercicios" VALUES (128,'chest','smith machine','https://v2.exercisedb.io/image/G4Lw1yHWIgEUSn','0759','smith incline shoulder raises','serratus anterior','"[\"deltoids\",\"trapezius\"]"','["Adjust the smith machine to an incline position.","Stand facing the machine with your feet shoulder-width apart.","Grasp the bar with an overhand grip, slightly wider than shoulder-width apart.","Keep your back straight and core engaged.","Raise the barbell up towards the ceiling, leading with your elbows.","Pause for a moment at the top, then slowly lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:10','2024-10-29 16:40:10');
INSERT INTO "ejercicios" VALUES (129,'back','stability ball','https://v2.exercisedb.io/image/EYar0dFMOtXLdc','1314','back extension on exercise ball','spine','"[\"glutes\",\"hamstrings\"]"','["Place the stability ball on the ground and lie face down on top of it, with your hips resting on the ball and your feet against a wall or other stable surface.","Position your hands behind your head or crossed over your chest.","Engage your core and slowly lift your upper body off the ball, extending your back until your body forms a straight line from your head to your heels.","Pause for a moment at the top, then slowly lower your upper body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (130,'back','band','https://v2.exercisedb.io/image/IAt5303sxBldrt','1010','band straight leg deadlift','spine','"[\"hamstrings\",\"glutes\"]"','["Stand with your feet shoulder-width apart and place the band around your feet.","Hold the band with both hands, palms facing your body, and keep your arms straight.","Engage your core and maintain a slight bend in your knees.","Slowly hinge forward at your hips, keeping your back straight and chest lifted.","Lower the band towards the ground while keeping your legs straight.","Pause for a moment at the bottom, then squeeze your glutes and hamstrings to return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (131,'back','stability ball','https://v2.exercisedb.io/image/bOmdq00s-emuW7','1333','exercise ball back extension with arms extended','spine','"[\"glutes\",\"hamstrings\"]"','["Place the stability ball on the ground and lie face down on top of it, with your hips resting on the ball and your feet against a wall for stability.","Extend your arms straight out in front of you, with your palms facing down.","Engage your core and slowly lift your upper body off the ball, keeping your back straight and your neck in line with your spine.","Pause for a moment at the top, then slowly lower your upper body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (132,'back','stability ball','https://v2.exercisedb.io/image/v7RSWC9XmhoioI','1334','exercise ball back extension with hands behind head','spine','"[\"glutes\",\"hamstrings\"]"','["Place the stability ball on the ground and lie face down on top of it with your hips resting on the ball.","Position your feet against a wall or other stable surface for support.","Cross your arms behind your head, with your hands touching the back of your head.","Engage your core and slowly lift your upper body off the ball, extending your back until your body forms a straight line from your head to your hips.","Pause for a moment at the top of the movement, then slowly lower your upper body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (133,'back','stability ball','https://v2.exercisedb.io/image/KFX-LxtxtzwkdG','1335','exercise ball back extension with knees off ground','spine','"[\"glutes\",\"hamstrings\"]"','["Place the stability ball on the ground and lie face down on top of it, with your hips resting on the ball and your feet against a wall for stability.","Position your hands behind your head or crossed over your chest.","Engage your core and slowly lift your upper body off the ball, extending your back until your body forms a straight line from your head to your heels.","Pause for a moment at the top, then slowly lower your upper body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (134,'back','stability ball','https://v2.exercisedb.io/image/Sh2fVixzCMIzvS','1336','exercise ball back extension with rotation','spine','"[\"glutes\",\"hamstrings\"]"','["Start by lying face down on the stability ball with your hips resting on the ball and your feet firmly planted on the ground.","Place your hands behind your head or cross them over your chest.","Engage your core and slowly lift your upper body off the ball, extending your back until your body forms a straight line from your head to your heels.","Pause for a moment at the top, then slowly rotate your torso to one side, keeping your hips and legs stable.","Return to the center and repeat the rotation to the other side.","Lower your upper body back down to the starting position and repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (135,'back','stability ball','https://v2.exercisedb.io/image/s8CaFiu1l90JKA','1338','exercise ball hug','spine','"[\"shoulders\",\"chest\"]"','["Sit on the stability ball with your feet flat on the ground and your back straight.","Hold the stability ball with both hands, hugging it close to your chest.","Engage your core muscles and slowly lean back, keeping your back straight and your feet planted on the ground.","Continue leaning back until you feel a stretch in your back muscles.","Hold the position for a few seconds, then slowly return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (136,'back','stability ball','https://v2.exercisedb.io/image/OrjjAEqONrNZ9A','1343','exercise ball prone leg raise','spine','"[\"abdominals\",\"glutes\"]"','["Lie face down on a mat with your legs extended and your toes resting on top of the stability ball.","Place your hands on the ground, shoulder-width apart, and engage your core muscles.","Keeping your legs straight, slowly lift them off the ground, using your lower back and glutes to raise them as high as possible.","Pause for a moment at the top, then slowly lower your legs back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (137,'back','body weight','https://v2.exercisedb.io/image/DvtMa2WXblKS00','0489','hyperextension','spine','"[\"glutes\",\"hamstrings\"]"','["Adjust the hyperextension bench so that your upper thighs are resting on the pad and your feet are secured.","Cross your arms over your chest or place your hands behind your head.","Lower your upper body towards the ground while keeping your back straight.","Pause for a moment at the bottom, then raise your upper body back up until it is in line with your legs.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (138,'back','body weight','https://v2.exercisedb.io/image/OFNTaN45pJNYtx','0488','hyperextension (on bench)','spine','"[\"glutes\",\"hamstrings\"]"','["Adjust the hyperextension bench so that your hips are resting comfortably on the pad and your feet are secured.","Cross your arms over your chest or place your hands behind your head.","Slowly lower your upper body towards the ground while keeping your back straight.","Pause for a moment at the bottom, then raise your upper body back up until it is in line with your legs.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:12','2024-10-29 16:40:12');
INSERT INTO "ejercicios" VALUES (139,'back','band','https://v2.exercisedb.io/image/Ono0JcL8tuDwMI','1018','band shrug','traps','"[\"shoulders\"]"','["Stand with your feet shoulder-width apart and place the band under your feet, holding the ends with your hands.","Keep your arms straight and relaxed, and let the band hang in front of your thighs.","Engage your traps by shrugging your shoulders upward, lifting the band as high as possible.","Hold the contraction for a moment, then slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (140,'back','barbell','https://v2.exercisedb.io/image/oKUJ5a0EdTBbxU','0095','barbell shrug','traps','"[\"shoulders\"]"','["Stand with your feet shoulder-width apart and hold a barbell in front of you with an overhand grip.","Keep your arms straight and your back straight throughout the exercise.","Lift your shoulders up towards your ears as high as possible, squeezing your traps at the top.","Hold for a moment, then slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (141,'back','cable','https://v2.exercisedb.io/image/QZUMUSkRI8yFmu','0220','cable shrug','traps','"[\"shoulders\"]"','["Stand facing the cable machine with your feet shoulder-width apart.","Grasp the cable handles with an overhand grip and let your arms hang down in front of you.","Keeping your arms straight, shrug your shoulders up towards your ears.","Hold the contraction for a moment, then slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (142,'back','dumbbell','https://v2.exercisedb.io/image/TLLrvvAZlui7bd','0305','dumbbell decline shrug','traps','"[\"shoulders\",\"rhomboids\"]"','["Set up a decline bench at a 45-degree angle.","Lie face down on the bench with your chest and stomach resting against it.","Hold a dumbbell in each hand with your arms fully extended towards the floor.","Keeping your arms straight, raise your shoulders towards your ears as high as possible.","Hold the contraction for a moment, then slowly lower the dumbbells back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (143,'back','dumbbell','https://v2.exercisedb.io/image/zOtVaOXMvIfdrb','0304','dumbbell decline shrug v. 2','traps','"[\"shoulders\",\"rhomboids\"]"','["Set up a decline bench at a 45-degree angle.","Lie face down on the bench with your chest and stomach resting on it.","Hold a dumbbell in each hand with your palms facing each other and your arms fully extended.","Keeping your arms straight, raise your shoulders as high as possible while squeezing your shoulder blades together.","Hold the contraction for a brief pause, then lower the dumbbells back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (144,'back','dumbbell','https://v2.exercisedb.io/image/V4ImpQJFeW4h4Z','0329','dumbbell incline shrug','traps','"[\"shoulders\"]"','["Set an incline bench to a 45-degree angle and sit on it with a dumbbell in each hand.","Place your feet flat on the ground and let your arms hang straight down with your palms facing your body.","Keeping your arms straight, shrug your shoulders up towards your ears as high as possible.","Hold the contraction for a moment, then slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (145,'back','dumbbell','https://v2.exercisedb.io/image/5Rf-hwevLQLPe9','0406','dumbbell shrug','traps','"[\"shoulders\"]"','["Stand with your feet shoulder-width apart and hold a dumbbell in each hand with your palms facing your body.","Keep your arms straight and let the dumbbells hang by your sides.","Raise your shoulders as high as possible, as if you are trying to touch your ears with your shoulders.","Hold the contraction for a second, then slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (146,'back','kettlebell','https://v2.exercisedb.io/image/uYsJDcPtnDKqap','0548','kettlebell sumo high pull','traps','"[\"shoulders\",\"glutes\",\"hamstrings\"]"','["Stand with your feet wider than shoulder-width apart, toes pointing outwards.","Hold a kettlebell with both hands in front of your body, arms extended downwards.","Bend your knees and lower your hips into a squat position, keeping your back straight.","Drive through your heels and explosively extend your hips and knees, pulling the kettlebell up towards your chin.","As you pull the kettlebell up, keep your elbows high and wide, and squeeze your shoulder blades together.","Lower the kettlebell back down to the starting position and repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (147,'back','leverage machine','https://v2.exercisedb.io/image/L9aG55tYjYsVVp','0580','lever gripless shrug','traps','"[\"shoulders\",\"forearms\"]"','["Adjust the seat height and position yourself on the leverage machine.","Grasp the handles with your palms facing inwards and your arms fully extended.","Keeping your back straight, exhale and elevate your shoulders as high as possible.","Hold the contraction for a brief moment, then inhale and slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (148,'back','leverage machine','https://v2.exercisedb.io/image/opT3oh8dOsSRSg','1439','lever gripless shrug v. 2','traps','"[\"shoulders\",\"forearms\"]"','["Adjust the seat height and position yourself on the machine with your back against the pad.","Grasp the handles or bars with an overhand grip, keeping your arms straight.","Keeping your back straight, lift your shoulders up towards your ears as high as possible.","Hold the contraction for a moment, then slowly lower your shoulders back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:13','2024-10-29 16:40:13');
INSERT INTO "ejercicios" VALUES (149,'upper arms','assisted','https://v2.exercisedb.io/image/74Yb5lLCQoCzwp','0018','assisted standing triceps extension (with towel)','triceps','"[\"shoulders\"]"','["Stand with your feet shoulder-width apart and hold a towel with both hands behind your head.","Keep your elbows close to your ears and your upper arms stationary.","Slowly extend your forearms upward, squeezing your triceps at the top.","Pause for a moment, then slowly lower the towel back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (150,'upper arms','leverage machine','https://v2.exercisedb.io/image/JKNOS0ICMQiJ51','0019','assisted triceps dip (kneeling)','triceps','"[\"chest\",\"shoulders\"]"','["Adjust the machine to your desired weight and height.","Kneel down on the pad facing the machine, with your hands gripping the handles.","Lower your body by bending your elbows, keeping your back straight and close to the machine.","Pause for a moment at the bottom, then push yourself back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (151,'upper arms','band','https://v2.exercisedb.io/image/tuzmgMPwPoyScN','0975','band close-grip push-up','triceps','"[\"chest\",\"shoulders\"]"','["Place a band around your upper arms, just above the elbows.","Assume a push-up position with your hands directly under your shoulders and your body in a straight line from head to heels.","Bend your elbows and lower your chest towards the ground, keeping your elbows close to your sides.","Push through your palms to extend your arms and return to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (152,'upper arms','band','https://v2.exercisedb.io/image/xm8FkGyHxrHxuQ','0998','band side triceps extension','triceps','"[\"shoulders\"]"','["Stand with your feet shoulder-width apart and hold the band with both hands, palms facing down.","Extend your arms straight out to the sides, keeping them parallel to the ground.","Slowly bend your elbows and bring your hands towards your shoulders, keeping your upper arms still.","Pause for a moment, then slowly extend your arms back out to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (153,'upper arms','barbell','https://v2.exercisedb.io/image/eYILIq2SmXm8Wx','0030','barbell close-grip bench press','triceps','"[\"chest\",\"shoulders\"]"','["Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.","Grasp the barbell with a close grip, slightly narrower than shoulder-width apart.","Unrack the barbell and lower it slowly towards your chest, keeping your elbows close to your body.","Pause for a moment when the barbell touches your chest.","Push the barbell back up to the starting position, fully extending your arms.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (154,'upper arms','barbell','https://v2.exercisedb.io/image/vYUoUoc44tJqcI','0035','barbell decline close grip to skull press','triceps','"[\"chest\",\"shoulders\"]"','["Lie on a decline bench with your head lower than your feet and hold a barbell with a close grip.","Lower the barbell towards your forehead by bending your elbows, keeping your upper arms stationary.","Pause for a moment, then extend your arms to press the barbell back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (155,'upper arms','barbell','https://v2.exercisedb.io/image/FUYgKUnSHzymHk','1719','barbell incline close grip bench press','triceps','"[\"chest\",\"shoulders\"]"','["Set up an incline bench at a 45-degree angle.","Lie down on the bench with your feet flat on the ground.","Grasp the barbell with a close grip, slightly narrower than shoulder-width apart.","Unrack the barbell and lower it slowly towards your chest, keeping your elbows close to your body.","Pause for a moment when the barbell touches your chest.","Push the barbell back up to the starting position, fully extending your arms.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (156,'upper arms','barbell','https://v2.exercisedb.io/image/Z8cfYWhFCZaYdC','0048','barbell incline reverse-grip press','triceps','"[\"chest\",\"shoulders\"]"','["Set up an incline bench at a 45-degree angle.","Lie back on the bench and grasp the barbell with a reverse grip, hands slightly wider than shoulder-width apart.","Unrack the barbell and lower it towards your upper chest, keeping your elbows tucked in.","Pause for a moment at the bottom, then push the barbell back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (157,'upper arms','barbell','https://v2.exercisedb.io/image/R3qMvbfDDg2aRH','0052','barbell jm bench press','triceps','"[\"chest\",\"shoulders\"]"','["Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.","Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.","Lower the barbell to your chest, keeping your elbows tucked in close to your body.","Push the barbell back up to the starting position, fully extending your arms.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (158,'upper arms','barbell','https://v2.exercisedb.io/image/F1wYp-i98K9T4R','1720','barbell lying back of the head tricep extension','triceps','"[\"shoulders\"]"','["Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.","Hold a barbell with an overhand grip, hands shoulder-width apart, and extend your arms straight up over your chest.","Keeping your upper arms stationary, slowly lower the barbell behind your head by bending your elbows.","Pause for a moment, then extend your arms back up to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:14','2024-10-29 16:40:14');
INSERT INTO "ejercicios" VALUES (159,'back','body weight','https://v2.exercisedb.io/image/5EC6s7NmVB763k','3297','back lever','upper back','"[\"biceps\",\"forearms\",\"core\"]"','["Start by hanging from a pull-up bar with an overhand grip, hands slightly wider than shoulder-width apart.","Engage your core and pull your shoulder blades down and back.","Bend your knees and tuck them towards your chest.","Slowly lift your legs up, keeping them straight, until your body is parallel to the ground.","Hold this position for a few seconds, then slowly lower your legs back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (160,'back','band','https://v2.exercisedb.io/image/HVbR0tQxQ-CXw1','0988','band one arm standing low row','upper back','"[\"biceps\",\"shoulders\"]"','["Attach the band to a stable anchor point at waist height.","Stand facing the anchor point with your feet shoulder-width apart.","Hold the band with one hand, palm facing inward, and step back to create tension in the band.","Bend your knees slightly and hinge forward at the hips, keeping your back straight.","Pull the band towards your waist, squeezing your shoulder blades together.","Pause for a moment at the top of the movement, then slowly release the band back to the starting position.","Repeat for the desired number of repetitions, then switch sides."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (161,'back','band','https://v2.exercisedb.io/image/pqx-2zRV4jEnOZ','0990','band one arm twisting seated row','upper back','"[\"biceps\",\"shoulders\"]"','["Sit on a bench or chair with your feet flat on the ground and your back straight.","Hold the band with one hand and extend your arm fully in front of you.","Keeping your back straight, pull the band towards your body by bending your elbow and squeezing your shoulder blades together.","At the same time, twist your torso towards the side of the pulling arm.","Pause for a moment at the top, then slowly release the tension in the band and return to the starting position.","Repeat for the desired number of repetitions, then switch to the other arm."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (162,'back','barbell','https://v2.exercisedb.io/image/T1E6Z7t351y3jw','0027','barbell bent over row','upper back','"[\"biceps\",\"forearms\"]"','["Stand with your feet shoulder-width apart and knees slightly bent.","Bend forward at the hips while keeping your back straight and chest up.","Grasp the barbell with an overhand grip, hands slightly wider than shoulder-width apart.","Pull the barbell towards your lower chest by retracting your shoulder blades and squeezing your back muscles.","Pause for a moment at the top, then slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (163,'back','barbell','https://v2.exercisedb.io/image/eRqZKd4kXHRoVA','0049','barbell incline row','upper back','"[\"biceps\",\"forearms\"]"','["Set up an incline bench at a 45-degree angle.","Lie face down on the bench with your chest against the pad and your feet flat on the ground.","Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.","Keep your back straight and your core engaged.","Pull the barbell towards your chest, squeezing your shoulder blades together.","Pause for a moment at the top, then slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (164,'back','barbell','https://v2.exercisedb.io/image/BUrxLSSoin2SrX','0064','barbell one arm bent over row','upper back','"[\"biceps\",\"forearms\"]"','["Stand with your feet shoulder-width apart, knees slightly bent, and hold a barbell with one hand using an overhand grip.","Bend forward at the hips, keeping your back straight and your head in a neutral position.","Pull the barbell up towards your chest, keeping your elbow close to your body and squeezing your shoulder blades together.","Lower the barbell back down to the starting position in a controlled manner.","Repeat for the desired number of repetitions, then switch to the other arm."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (165,'back','barbell','https://v2.exercisedb.io/image/GtZuTzQwRN8jMl','3017','barbell pendlay row','upper back','"[\"biceps\",\"forearms\"]"','["Stand with your feet shoulder-width apart and your knees slightly bent.","Bend forward at the hips, keeping your back straight and your chest up.","Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.","Pull the barbell towards your upper abdomen, squeezing your shoulder blades together.","Lower the barbell back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (166,'back','barbell','https://v2.exercisedb.io/image/-yulvW9alMfMGE','0118','barbell reverse grip bent over row','upper back','"[\"biceps\",\"forearms\"]"','["Stand with your feet shoulder-width apart and knees slightly bent.","Hold a barbell with an overhand grip, palms facing down, and hands slightly wider than shoulder-width apart.","Bend forward at the hips, keeping your back straight and chest up, until your torso is almost parallel to the floor.","Pull the barbell towards your lower chest, squeezing your shoulder blades together.","Pause for a moment at the top, then slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (167,'back','barbell','https://v2.exercisedb.io/image/9t46Z3JeboQ7RC','1317','barbell reverse grip incline bench row','upper back','"[\"biceps\",\"forearms\"]"','["Set up an incline bench at a 45-degree angle.","Sit on the bench facing the backrest with your chest against it.","Grab the barbell with a reverse grip (palms facing down) and hands slightly wider than shoulder-width apart.","Keep your back straight and core engaged.","Pull the barbell towards your upper abdomen, squeezing your shoulder blades together.","Pause for a moment at the top of the movement.","Slowly lower the barbell back to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
INSERT INTO "ejercicios" VALUES (168,'back','body weight','https://v2.exercisedb.io/image/AOqc4xr5upZD57','3168','bodyweight squatting row','upper back','"[\"biceps\",\"shoulders\"]"','["Stand with your feet shoulder-width apart, holding onto a sturdy object or suspension trainer with your arms extended.","Lower your body into a squat position, keeping your back straight and your knees behind your toes.","From the squat position, pull your body up towards the object or suspension trainer, squeezing your shoulder blades together.","Pause for a moment at the top, then slowly lower your body back down to the starting position.","Repeat for the desired number of repetitions."]','2024-10-29 16:40:15','2024-10-29 16:40:15');
